import React, { useState, useCallback } from 'react';
import { Download, Filter, Calendar } from 'lucide-react';
import { formatCurrency } from '@/utils/money';
import { formatDate } from '@/utils/date';
import { Button } from '@/components/ui/Button';
import { Transaction } from '@/types/contribution';
import { DonationExportModal } from '@/components/contribution/DonationExportModal';

interface DonationHistoryProps {
  donations: Transaction[];
}

export const DonationHistory: React.FC<DonationHistoryProps> = ({ donations }) => {
  const [showExportModal, setShowExportModal] = useState(false);
  const [timeFilter, setTimeFilter] = useState('all');

  const handleTimeFilterChange = useCallback((e: React.ChangeEvent<HTMLSelectElement>) => {
    setTimeFilter(e.target.value);
  }, []);

  const handleShowExportModal = useCallback(() => {
    setShowExportModal(true);
  }, []);

  const handleCloseExportModal = useCallback(() => {
    setShowExportModal(false);
  }, []);

  const filteredDonations = donations.filter(donation => {
    if (timeFilter === 'all') return true;

    const donationDate = new Date(donation.timestamp);
    const now = new Date();

    switch (timeFilter) {
      case 'week': {
        const weekAgo = new Date();
        weekAgo.setDate(now.getDate() - 7);
        return donationDate >= weekAgo;
      }
      case 'month': {
        const monthAgo = new Date();
        monthAgo.setMonth(now.getMonth() - 1);
        return donationDate >= monthAgo;
      }
      case 'year': {
        const yearAgo = new Date();
        yearAgo.setFullYear(now.getFullYear() - 1);
        return donationDate >= yearAgo;
      }
      default:
        return true;
    }
  });

  return (
    <div className="bg-white rounded-lg shadow-md">
      <div className="p-6 border-b border-gray-200">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-4 sm:space-y-0">
          <h2 className="text-xl font-semibold text-gray-900">Donation History</h2>
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <Calendar className="h-5 w-5 text-gray-400" />
              <select
                value={timeFilter}
                onChange={handleTimeFilterChange}
                className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                aria-label="Filter by time period"
              >
                <option value="all">All Time</option>
                <option value="week">Past Week</option>
                <option value="month">Past Month</option>
                <option value="year">Past Year</option>
              </select>
            </div>
            <Button
              onClick={handleShowExportModal}
              variant="secondary"
              className="flex items-center"
            >
              <Download className="h-4 w-4 mr-2" />
              Export CSV
            </Button>
          </div>
        </div>
      </div>
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead>
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Charity</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Amount</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Fiat Value</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Transaction ID</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {filteredDonations.map((donation) => (
              <tr key={donation.id}>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {formatDate(donation.timestamp, true)}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {donation.metadata?.organization || 'Unknown'}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {donation.amount} {donation.cryptoType || 'GLMR'}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {donation.fiatValue ? formatCurrency(donation.fiatValue) : 'N/A'}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  {donation.hash ? (
                    <a 
                      href={`https://moonscan.io/tx/${donation.hash}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-600 hover:text-indigo-900 truncate block max-w-xs"
                    >
                      {donation.hash.substring(0, 10)}...
                    </a>
                  ) : (
                    'N/A'
                  )}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                    donation.status === 'completed' 
                      ? 'bg-green-100 text-green-800'
                      : donation.status === 'pending'
                      ? 'bg-yellow-100 text-yellow-800'
                      : 'bg-red-100 text-red-800'
                  }`}>
                    {donation.status.charAt(0).toUpperCase() + donation.status.slice(1)}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Export Modal */}
      {showExportModal && (
        <DonationExportModal
          donations={donations}
          onClose={handleCloseExportModal}
        />
      )}
    </div>
  );
};

import React, { useState, useEffect, useCallback } from 'react';
import { useScheduledDonation } from '@/hooks/web3/useScheduledDonation';
import { Card } from '@/components/ui/Card';
import { Button } from '@/components/ui/Button';
import { LoadingSpinner } from '@/components/ui/LoadingSpinner';
import { Calendar, AlertTriangle, XCircle } from 'lucide-react';
import { formatDate } from '@/utils/date';
import { useToast } from '@/contexts/ToastContext';
import { Logger } from '@/utils/logger';

interface ScheduledDonation {
  id: number;
  charity: string;
  token: string;
  totalAmount: string;
  amountPerMonth: string;
  monthsRemaining: number;
  nextDistribution: Date;
  active: boolean;
}

export const ScheduledDonations: React.FC = () => {
  const { getDonorSchedules, cancelSchedule, loading, error } = useScheduledDonation();
  const [schedules, setSchedules] = useState<ScheduledDonation[]>([]);
  const [loadingSchedules, setLoadingSchedules] = useState(true);
  const [selectedSchedule, setSelectedSchedule] = useState<ScheduledDonation | null>(null);
  const [isCancelModalOpen, setIsCancelModalOpen] = useState(false);
  const [cancelError, setCancelError] = useState<string | null>(null);
  const { showToast } = useToast();

  useEffect(() => {
    fetchSchedules();
  }, []);

  const fetchSchedules = async () => {
    try {
      setLoadingSchedules(true);
      const donorSchedules = await getDonorSchedules();
      setSchedules(donorSchedules);
    } catch (err) {
      Logger.error('Failed to fetch scheduled donations:', err);
    } finally {
      setLoadingSchedules(false);
    }
  };

  const handleCancelClick = useCallback((schedule: ScheduledDonation) => {
    setSelectedSchedule(schedule);
    setCancelError(null);
    setIsCancelModalOpen(true);
  }, []);

  const handleCloseModal = useCallback(() => {
    setIsCancelModalOpen(false);
  }, []);

  const createCancelHandler = useCallback((schedule: ScheduledDonation) => {
    return () => handleCancelClick(schedule);
  }, [handleCancelClick]);

  const handleConfirmCancel = async () => {
    if (!selectedSchedule) return;

    try {
      setCancelError(null);
      await cancelSchedule(selectedSchedule.id);
      showToast('success', 'Scheduled donation cancelled', 'Your monthly donation schedule has been cancelled and remaining funds returned to your wallet.');
      setIsCancelModalOpen(false);
      fetchSchedules();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to cancel scheduled donation';
      setCancelError(errorMessage);

      // Check if user rejected transaction
      if (errorMessage.includes('user rejected') || errorMessage.includes('User denied')) {
        setCancelError('Transaction was rejected. Please confirm the transaction in your wallet to cancel the schedule.');
      }
    }
  };

  if (loadingSchedules) {
    return (
      <div className="flex justify-center items-center py-8">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 text-red-700 p-4 rounded-lg">
        <div className="flex">
          <AlertTriangle className="h-5 w-5 text-red-500 mr-2" />
          <span>{error}</span>
        </div>
      </div>
    );
  }

  if (schedules.length === 0) {
    return (
      <Card className="p-6">
        <div className="text-center py-8">
          <Calendar className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Scheduled Donations</h3>
          <p className="text-gray-500 mb-4">You don&apos;t have any active monthly donation schedules.</p>
        </div>
      </Card>
    );
  }

  return (
    <div>
      <Card className="mb-6">
        <div className="p-6 border-b border-gray-200">
          <div className="flex justify-between items-center">
            <h2 className="text-xl font-semibold text-gray-900">Monthly Donation Schedules</h2>
            <Button
              variant="secondary"
              onClick={fetchSchedules}
              disabled={loading}
            >
              {loading ? 'Refreshing...' : 'Refresh'}
            </Button>
          </div>
        </div>
        <div className="divide-y divide-gray-200">
          {schedules.map((schedule) => (
            <div key={schedule.id} className="p-6">
              <div className="flex flex-col md:flex-row md:justify-between md:items-start">
                <div>
                  <div className="flex items-center mb-2">
                    <Calendar className="h-5 w-5 text-indigo-500 mr-2" />
                    <h3 className="text-lg font-medium text-gray-900">Monthly Donation to {schedule.charity.substring(0, 6)}&hellip;{schedule.charity.substring(38)}</h3>
                  </div>
                  <div className="space-y-1 text-sm text-gray-500">
                    <p>Total Amount: {schedule.totalAmount} tokens</p>
                    <p>Monthly Payment: {schedule.amountPerMonth} tokens</p>
                    <p>Months Remaining: {schedule.monthsRemaining} of 12</p>
                    <p>Next Distribution: {formatDate(schedule.nextDistribution.toISOString())}</p>
                  </div>
                </div>
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={createCancelHandler(schedule)}
                  disabled={loading}
                  className="mt-4 md:mt-0"
                >
                  Cancel Schedule
                </Button>
              </div>
            </div>
          ))}
        </div>
      </Card>

      {/* Cancel Confirmation Modal */}
      {isCancelModalOpen && selectedSchedule && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full">
            <div className="p-6">
              <div className="flex items-center justify-center mb-4">
                <div className="bg-red-100 rounded-full p-3">
                  <AlertTriangle className="h-6 w-6 text-red-600" />
                </div>
              </div>
              <h3 className="text-lg font-medium text-gray-900 text-center mb-2">Confirm Cancellation</h3>
              <p className="text-sm text-gray-500 text-center mb-6">
                Are you sure you want to cancel your monthly donation schedule? The remaining funds ({parseFloat(selectedSchedule.amountPerMonth) * selectedSchedule.monthsRemaining} tokens) will be returned to your wallet.
              </p>

              {cancelError && (
                <div className="mb-4 p-3 bg-red-50 text-red-600 rounded-md">
                  {cancelError}
                </div>
              )}

              <div className="flex justify-center space-x-3">
                <Button
                  variant="secondary"
                  onClick={handleCloseModal}
                >
                  Keep Schedule
                </Button>
                <Button
                  variant="danger"
                  onClick={handleConfirmCancel}
                  disabled={loading}
                >
                  {loading ? 'Processing...' : 'Cancel Schedule'}
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

import React, { useState, useCallback } from "react";
import { useWalletAlias } from "@/hooks/useWalletAlias";
import { useWeb3 } from "@/contexts/Web3Context";
import { Button } from "@/components/ui/Button";
import { Input } from "@/components/ui/Input";
import { Card } from "@/components/ui/Card";
import { Wallet, Edit, Trash2, AlertCircle, Check } from "lucide-react";
import { shortenAddress } from "@/utils/web3";

export const WalletAliasSettings: React.FC = () => {
  const { address, isConnected } = useWeb3();
  const { alias, aliases, loading, error, setWalletAlias, deleteWalletAlias } =
    useWalletAlias();
  const [newAlias, setNewAlias] = useState("");
  const [editMode, setEditMode] = useState(false);
  const [validationError, setValidationError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setValidationError(null);

    // Validate alias
    if (!newAlias.trim()) {
      setValidationError("Alias cannot be empty");
      return;
    }

    if (newAlias.length < 3 || newAlias.length > 20) {
      setValidationError("Alias must be between 3 and 20 characters");
      return;
    }

    if (!/^[a-zA-Z0-9_-]+$/.test(newAlias)) {
      setValidationError(
        "Alias can only contain letters, numbers, underscores, and hyphens",
      );
      return;
    }

    const success = await setWalletAlias(newAlias);
    if (success) {
      setNewAlias("");
      setEditMode(false);
    }
  };

  const handleEdit = () => {
    setNewAlias(alias || "");
    setEditMode(true);
    setValidationError(null);
  };

  const handleCancel = () => {
    setNewAlias("");
    setEditMode(false);
    setValidationError(null);
  };

  const handleDelete = async (aliasId: string) => {
    if (confirm("Are you sure you want to delete this wallet alias?")) {
      await deleteWalletAlias(aliasId);
    }
  };

  // Callback handlers for better performance
  const handleStartEdit = useCallback(() => setEditMode(true), []);
  const createDeleteHandler = useCallback(
    (aliasId: string) => {
      return () => handleDelete(aliasId);
    },
    [handleDelete],
  );

  if (!isConnected) {
    return (
      <Card className="p-6">
        <div className="flex items-center mb-4">
          <Wallet className="h-5 w-5 text-gray-500 mr-2" />
          <h2 className="text-lg font-medium text-gray-900">Wallet Alias</h2>
        </div>
        <p className="text-gray-600 mb-4">
          Connect your wallet to set a public alias that will be displayed on
          the contribution tracker.
        </p>
      </Card>
    );
  }

  return (
    <Card className="p-6">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center">
          <Wallet className="h-5 w-5 text-gray-500 mr-2" />
          <h2 className="text-lg font-medium text-gray-900">Wallet Alias</h2>
        </div>
        {!editMode && alias && (
          <Button
            variant="secondary"
            size="sm"
            onClick={handleEdit}
            className="flex items-center"
          >
            <Edit className="h-4 w-4 mr-1" />
            Edit
          </Button>
        )}
      </div>

      {error && (
        <div className="mb-4 p-3 bg-red-50 text-red-600 rounded-md flex items-start">
          <AlertCircle className="h-5 w-5 text-red-500 mt-0.5 mr-2 flex-shrink-0" />
          <span>{error}</span>
        </div>
      )}

      <div className="mb-4">
        <p className="text-gray-600 mb-2">
          Set a public alias for your wallet address. This alias will be
          displayed on the contribution tracker instead of your wallet address.
        </p>
        <div className="bg-gray-50 p-3 rounded-md">
          <p className="text-sm text-gray-500">Current Wallet</p>
          <p className="font-mono text-gray-900">
            {shortenAddress(address || "")}
          </p>
        </div>
      </div>

      {editMode ? (
        <form onSubmit={handleSubmit} className="space-y-4">
          <Input
            label="Wallet Alias"
            value={newAlias}
            onChange={(e) => setNewAlias(e.target.value)}
            placeholder="Enter a public alias for your wallet"
            error={validationError || undefined}
          />
          <div className="flex space-x-3">
            <Button
              type="submit"
              disabled={loading}
              className="flex items-center"
            >
              <Check className="h-4 w-4 mr-1" />
              {loading ? "Saving..." : "Save Alias"}
            </Button>
            <Button
              type="button"
              variant="secondary"
              onClick={handleCancel}
              disabled={loading}
            >
              Cancel
            </Button>
          </div>
        </form>
      ) : (
        <div>
          {alias ? (
            <div className="bg-indigo-50 p-3 rounded-md mb-4">
              <p className="text-sm text-gray-500">Current Alias</p>
              <p className="font-medium text-indigo-900">{alias}</p>
            </div>
          ) : (
            <div className="mb-4">
              <p className="text-gray-600">
                You haven&apos;t set an alias for this wallet yet. Set an alias
                to make your contributions more recognizable.
              </p>
              <Button onClick={handleStartEdit} className="mt-3">
                Set Wallet Alias
              </Button>
            </div>
          )}
        </div>
      )}

      {aliases.length > 0 && (
        <div className="mt-6">
          <h3 className="text-md font-medium text-gray-900 mb-3">
            Your Wallet Aliases
          </h3>
          <div className="space-y-3">
            {aliases.map((item) => (
              <div
                key={item.id}
                className="flex justify-between items-center p-3 bg-gray-50 rounded-md"
              >
                <div>
                  <p className="font-medium text-gray-900">{item.alias}</p>
                  <p className="text-xs text-gray-500 font-mono">
                    {shortenAddress(item.walletAddress)}
                  </p>
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={createDeleteHandler(item.id)}
                  className="text-red-500 hover:text-red-700 hover:bg-red-50"
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            ))}
          </div>
        </div>
      )}
    </Card>
  );
};

import React, { useCallback } from 'react';
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import Link from '@tiptap/extension-link';
import { Bold, Italic, Link as LinkIcon, List, ListOrdered, Heading1, Heading2, Undo, Redo } from 'lucide-react';
import { Button } from './Button';
import { cn } from '@/utils/cn';

interface EditorProps {
  content: string;
  onChange: (content: string) => void;
  className?: string;
  placeholder?: string;
}

interface MenuButtonProps {
  onClick: () => void;
  active?: boolean;
  icon: React.ComponentType<{ className?: string }>;
  title: string;
}

export const Editor: React.FC<EditorProps> = ({
  content,
  onChange,
  className,
  placeholder = 'Start writing...'
}) => {
  const editor = useEditor({
    extensions: [
      StarterKit,
      Link.configure({
        openOnClick: false,
        HTMLAttributes: {
          class: 'text-indigo-600 hover:text-indigo-800 underline'
        }
      })
    ],
    content,
    onUpdate: ({ editor }) => {
      onChange(editor.getHTML());
    },
    editorProps: {
      attributes: {
        class: 'prose prose-sm sm:prose lg:prose-lg xl:prose-xl focus:outline-none max-w-none',
        placeholder
      }
    }
  });

  const handleBold = useCallback(() => {
    editor?.chain().focus().toggleBold().run();
  }, [editor]);

  const handleItalic = useCallback(() => {
    editor?.chain().focus().toggleItalic().run();
  }, [editor]);

  const handleHeading1 = useCallback(() => {
    editor?.chain().focus().toggleHeading({ level: 1 }).run();
  }, [editor]);

  const handleHeading2 = useCallback(() => {
    editor?.chain().focus().toggleHeading({ level: 2 }).run();
  }, [editor]);

  const handleBulletList = useCallback(() => {
    editor?.chain().focus().toggleBulletList().run();
  }, [editor]);

  const handleOrderedList = useCallback(() => {
    editor?.chain().focus().toggleOrderedList().run();
  }, [editor]);

  const handleLink = useCallback(() => {
    const url = window.prompt('Enter URL');
    if (url) {
      editor?.chain().focus().setLink({ href: url }).run();
    }
  }, [editor]);

  const handleUndo = useCallback(() => {
    editor?.chain().focus().undo().run();
  }, [editor]);

  const handleRedo = useCallback(() => {
    editor?.chain().focus().redo().run();
  }, [editor]);

  if (!editor) {
    return null;
  }

  const MenuButton = ({ onClick, active, icon: Icon, title }: MenuButtonProps) => (
    <Button
      type="button"
      variant="secondary"
      onClick={onClick}
      className={cn(
        'p-2',
        active && 'bg-indigo-100 text-indigo-900'
      )}
      title={title}
    >
      <Icon className="h-4 w-4" />
    </Button>
  );

  return (
    <div className={cn('border border-gray-200 rounded-lg', className)}>
      <div className="border-b border-gray-200 p-2 flex flex-wrap gap-1">
        <MenuButton
          onClick={handleBold}
          active={editor.isActive('bold')}
          icon={Bold}
          title="Bold"
        />
        <MenuButton
          onClick={handleItalic}
          active={editor.isActive('italic')}
          icon={Italic}
          title="Italic"
        />
        <MenuButton
          onClick={handleHeading1}
          active={editor.isActive('heading', { level: 1 })}
          icon={Heading1}
          title="Heading 1"
        />
        <MenuButton
          onClick={handleHeading2}
          active={editor.isActive('heading', { level: 2 })}
          icon={Heading2}
          title="Heading 2"
        />
        <MenuButton
          onClick={handleBulletList}
          active={editor.isActive('bulletList')}
          icon={List}
          title="Bullet List"
        />
        <MenuButton
          onClick={handleOrderedList}
          active={editor.isActive('orderedList')}
          icon={ListOrdered}
          title="Numbered List"
        />
        <MenuButton
          onClick={handleLink}
          active={editor.isActive('link')}
          icon={LinkIcon}
          title="Add Link"
        />
        <div className="ml-auto flex gap-1">
          <MenuButton
            onClick={handleUndo}
            icon={Undo}
            title="Undo"
          />
          <MenuButton
            onClick={handleRedo}
            icon={Redo}
            title="Redo"
          />
        </div>
      </div>
      <EditorContent
        editor={editor}
        className="p-4 min-h-[200px] max-h-[600px] overflow-y-auto bg-indigo-50"
      />
    </div>
  );
};

import React from 'react';
import { cn } from '@/utils/cn';

interface TabsProps {
  defaultValue: string;
  children: React.ReactNode;
  className?: string;
}

interface TabsListProps {
  children: React.ReactNode;
  className?: string;
}

interface TabsTriggerProps {
  value: string;
  children: React.ReactNode;
  className?: string;
}

interface TabsContentProps {
  value: string;
  children: React.ReactNode;
  className?: string;
}

const TabsContext = React.createContext<{
  value: string;
  onChange: (value: string) => void;
} | null>(null);

export const Tabs: React.FC<TabsProps> = ({ defaultValue, children, className }) => {
  const [value, setValue] = React.useState(defaultValue);

  return (
    <TabsContext.Provider value={{ value, onChange: setValue }}>
      <div className={cn('space-y-2', className)}>
        {children}
      </div>
    </TabsContext.Provider>
  );
};

export const TabsList: React.FC<TabsListProps> = ({ children, className }) => {
  return (
    <div className={cn('flex space-x-1 rounded-lg bg-gray-100 p-1', className)}>
      {children}
    </div>
  );
};

export const TabsTrigger: React.FC<TabsTriggerProps> = ({ value, children, className }) => {
  const context = React.useContext(TabsContext);
  if (!context) throw new Error('TabsTrigger must be used within Tabs');

  const isActive = context.value === value;

  return (
    <button
      onClick={() => context.onChange(value)}
      className={cn(
        'px-3 py-1.5 text-sm font-medium rounded-md transition-all',
        isActive
          ? 'bg-white text-gray-900 shadow'
          : 'text-gray-600 hover:text-gray-900',
        className
      )}
    >
      {children}
    </button>
  );
};

export const TabsContent: React.FC<TabsContentProps> = ({ value, children, className }) => {
  const context = React.useContext(TabsContext);
  if (!context) throw new Error('TabsContent must be used within Tabs');

  if (context.value !== value) return null;

  return (
    <div className={className}>
      {children}
    </div>
  );
};

import React, { useState } from 'react';
import { CheckCircle, X, ExternalLink } from 'lucide-react';
import { Button } from '@/components/ui/Button';
import { useVolunteerVerification } from '@/hooks/useVolunteerVerification';
import { useTranslation } from '@/hooks/useTranslation';
import { Logger } from '@/utils/logger';

interface ApplicationAcceptanceProps {
  applicationId: string;
  applicantName: string;
  opportunityTitle: string;
  onAccepted?: (hash: string) => void;
}

export const ApplicationAcceptance: React.FC<ApplicationAcceptanceProps> = ({
  applicationId,
  applicantName,
  opportunityTitle,
  onAccepted
}) => {
  const { acceptApplication, loading, error } = useVolunteerVerification();
  const [acceptanceHash, setAcceptanceHash] = useState<string | null>(null);
  const [isAccepted, setIsAccepted] = useState(false);
  const { t } = useTranslation();

  const handleAccept = async () => {
    try {
      const hash = await acceptApplication(applicationId);
      if (hash) {
        setAcceptanceHash(hash);
        setIsAccepted(true);
        onAccepted?.(hash);
      }
    } catch (err) {
      Logger.error('Acceptance failed:', err);
    }
  };

  if (isAccepted) {
    return (
      <div className="bg-green-50 border border-green-200 rounded-lg p-4">
        <div className="flex items-center mb-2">
          <CheckCircle className="h-5 w-5 text-green-500 mr-2" />
          <h3 className="text-lg font-medium text-green-800">{t('volunteer.applicationAccepted', 'Application Accepted')}</h3>
        </div>
        <p className="text-sm text-green-700 mb-3">
          {t('volunteer.applicationRecorded', 'The volunteer application has been accepted and recorded on the blockchain.')}
        </p>
        {acceptanceHash && (
          <div className="bg-white p-3 rounded border border-green-200">
            <p className="text-xs text-gray-500 mb-1">{t('volunteer.acceptanceHash', 'Acceptance Hash')}</p>
            <div className="flex items-center">
              <code className="text-xs font-mono text-gray-800 break-all">
                {acceptanceHash}
              </code>
              <a 
                href={`https://moonbase.moonscan.io/tx/${acceptanceHash}`}
                target="_blank"
                rel="noopener noreferrer"
                className="ml-2 text-indigo-600 hover:text-indigo-800"
              >
                <ExternalLink className="h-3 w-3" />
              </a>
            </div>
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="bg-white border border-gray-200 rounded-lg p-4">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="text-lg font-medium text-gray-900">{applicantName}</h3>
          <p className="text-sm text-gray-500">
            {t('volunteer.appliedFor')}: {opportunityTitle}
          </p>
        </div>
        <div className="flex space-x-2">
          <Button
            onClick={handleAccept}
            disabled={loading}
            className="flex items-center"
          >
            <CheckCircle className="h-4 w-4 mr-2" />
            {loading ? t('volunteer.processing', 'Processing...') : t('volunteer.accept')}
          </Button>
          <Button
            variant="secondary"
            className="flex items-center"
          >
            <X className="h-4 w-4 mr-2" />
            {t('volunteer.reject')}
          </Button>
        </div>
      </div>

      {error && (
        <div className="p-3 bg-red-50 text-red-700 text-sm rounded-md">
          {error}
        </div>
      )}
    </div>
  );
};

