import React, { createContext, useContext, useState, useCallback } from 'react';
import { Toast, ToastType } from '../components/ui/Toast';
import { SecureRandom } from '@/utils/security/index';

interface ToastContextType {
  showToast: (type: ToastType, title: string, message?: string) => void;
}

// Create context with undefined initial value but proper type
export const ToastContext = createContext<ToastContextType | undefined>(undefined);

/**
 * Provides toast notification functionality to child components
 * @param children - React child elements
 * @returns JSX element with toast context provider and toast display
 */
export function ToastProvider({ children }: { children: React.ReactNode }) {
  const [toasts, setToasts] = useState<Array<{
    id: string;
    type: ToastType;
    title: string;
    message?: string;
  }>>([]);

  const showToast = useCallback((type: ToastType, title: string, message?: string) => {
    const id = SecureRandom.generateSecureId();
    setToasts(prev => [...prev, { id, type, title, message }]);

    if (type !== 'loading') {
      setTimeout(() => {
        setToasts(prev => prev.filter(t => t.id !== id));
      }, 5000);
    }
  }, []);

  const removeToast = useCallback((id: string) => {
    setToasts(prev => prev.filter(t => t.id !== id));
  }, []);

  const createRemoveHandler = useCallback((id: string) => {
    return () => removeToast(id);
  }, [removeToast]);

  return (
    <ToastContext.Provider value={{ showToast }}>
      {children}
      <div className="fixed bottom-0 right-0 p-4 space-y-4 z-50 pointer-events-none">
        {toasts.map(toast => (
          <Toast
            key={toast.id}
            type={toast.type}
            title={toast.title}
            message={toast.message}
            onClose={createRemoveHandler(toast.id)}
          />
        ))}
      </div>
    </ToastContext.Provider>
  );
}

/**
 * Hook to access toast notification functionality
 * @returns Object containing showToast function for displaying toast notifications
 * @throws {Error} When used outside of ToastProvider
 */
export function useToast() {
  const context = useContext(ToastContext);
  if (!context) {
    throw new Error('useToast must be used within ToastProvider');
  }
  return context;
}

import React, {
  createContext,
  useContext,
  useState,
  useCallback,
  useEffect,
} from "react";
import { ethers } from "ethers";
import { Logger } from "@/utils/logger";
import { CHAIN_IDS } from "@/config/contracts";

// Error type guards for Web3 errors
interface WalletError {
  code?: number;
  message?: string;
}

/**
 * Type guard to check if an error is a wallet error with code and message properties
 * @param error - The error object to check
 * @returns True if the error is a wallet error, false otherwise
 */
function isWalletError(error: unknown): error is WalletError {
  return typeof error === "object" && error !== null;
}

/**
 * Checks if an error has a specific error code
 * @param error - The error object to check
 * @param code - The error code to match
 * @returns True if the error has the specified code, false otherwise
 */
function hasErrorCode(error: unknown, code: number): boolean {
  return isWalletError(error) && error.code === code;
}

/**
 * Checks if an error message contains a specific substring
 * @param error - The error object to check
 * @param message - The message substring to search for
 * @returns True if the error message contains the substring, false otherwise
 */
function hasErrorMessage(error: unknown, message: string): boolean {
  return (
    isWalletError(error) &&
    typeof error.message === "string" &&
    error.message.includes(message)
  );
}

interface Web3ContextType {
  provider: ethers.Provider | null;
  address: string | null;
  chainId: number | null;
  isConnected: boolean;
  isConnecting: boolean;
  connect: () => Promise<void>;
  disconnect: () => Promise<void>;
  error: Error | null;
  switchChain: (chainId: number) => Promise<void>;
}

const Web3Context = createContext<Web3ContextType | undefined>(undefined);

const MOONBASE_CHAIN_INFO = {
  chainId: `0x${CHAIN_IDS.MOONBASE.toString(16)}`,
  chainName: "Moonbase Alpha",
  nativeCurrency: {
    name: "DEV",
    symbol: "DEV",
    decimals: 18,
  },
  rpcUrls: ["https://rpc.api.moonbase.moonbeam.network"],
  blockExplorerUrls: ["https://moonbase.moonscan.io/"],
};

export function Web3Provider({ children }: { children: React.ReactNode }) {
  const [provider, setProvider] = useState<ethers.Provider | null>(null);
  const [address, setAddress] = useState<string | null>(null);
  const [chainId, setChainId] = useState<number | null>(null);
  const [isConnecting, setIsConnecting] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  // Handle account changes
  const handleAccountsChanged = useCallback((accounts: string[]) => {
    if (accounts.length === 0) {
      // User disconnected their wallet
      setAddress(null);
      setProvider(null);
      setChainId(null);
      Logger.info("Wallet disconnected");
    } else {
      setAddress(accounts[0]);
      Logger.info("Account changed", { address: accounts[0] });
    }
  }, []);

  // Handle chain changes
  const handleChainChanged = useCallback((chainIdHex: string) => {
    const newChainId = parseInt(chainIdHex, 16);
    setChainId(newChainId);
    Logger.info("Chain changed", { chainId: newChainId });

    // Reload the page when chain changes to ensure all state is fresh
    window.location.reload();
  }, []);

  // Initialize provider and check for existing connection
  useEffect(() => {
    const initProvider = async () => {
      if (typeof window.ethereum !== "undefined") {
        try {
          // Check if already connected
          const accounts = await window.ethereum.request({
            method: "eth_accounts",
          });
          if (accounts.length > 0) {
            const provider = new ethers.BrowserProvider(window.ethereum);
            const network = await provider.getNetwork();

            setProvider(provider);
            setAddress(accounts[0]);
            setChainId(Number(network.chainId));
            Logger.info("Restored existing connection", {
              address: accounts[0],
              chainId: network.chainId,
            });
          }
        } catch (err: unknown) {
          // Clear any existing connection state
          setProvider(null);
          setAddress(null);
          setChainId(null);

          // Handle unauthorized error specifically
          if (hasErrorMessage(err, "has not been authorized")) {
            const error = new Error(
              'Wallet connection needs authorization. Please click "Connect" to continue.',
            );
            setError(error);
            Logger.info("Wallet needs reauthorization");
          } else {
            Logger.error("Failed to restore connection", { error: err });
          }
        }
      }
    };

    initProvider();
  }, []);

  // Set up event listeners
  useEffect(() => {
    if (typeof window.ethereum !== "undefined") {
      window.ethereum.on("accountsChanged", handleAccountsChanged);
      window.ethereum.on("chainChanged", handleChainChanged);
      window.ethereum.on("disconnect", () => {
        setProvider(null);
        setAddress(null);
        setChainId(null);
      });

      return () => {
        if (window.ethereum?.removeListener) {
          window.ethereum.removeListener(
            "accountsChanged",
            handleAccountsChanged,
          );
          window.ethereum.removeListener("chainChanged", handleChainChanged);
          window.ethereum.removeListener("disconnect", () => {});
        }
      };
    }
  }, [handleAccountsChanged, handleChainChanged]);

  const connect = useCallback(async () => {
    if (typeof window.ethereum === "undefined") {
      const error = new Error("Please install MetaMask to connect");
      Logger.error("MetaMask not found", { error });
      setError(error);
      throw error;
    }

    try {
      setIsConnecting(true);
      setError(null);

      // Request account access
      const accounts = await window.ethereum.request({
        method: "eth_requestAccounts",
      });

      if (!accounts || accounts.length === 0) {
        throw new Error("No accounts found");
      }

      // Create Web3 provider
      const provider = new ethers.BrowserProvider(window.ethereum);

      // Get connected chain ID
      const network = await provider.getNetwork();
      const currentChainId = Number(network.chainId);

      // Set provider first so it's available for chain switching
      setProvider(provider);

      // Switch to Moonbase Alpha if on wrong network
      if (currentChainId !== CHAIN_IDS.MOONBASE) {
        try {
          await switchChain(CHAIN_IDS.MOONBASE);
        } catch (switchError: unknown) {
          // If user rejected the switch, throw error
          if (switchError?.code === 4001) {
            throw new Error("Please switch to Moonbase Alpha (TestNet)");
          }
          // For other errors, log warning but continue
          Logger.warn("Failed to switch to Moonbase Alpha", {
            error: switchError,
          });
        }
      }

      // Get chain ID again in case it changed
      const finalNetwork = await provider.getNetwork();
      setChainId(Number(finalNetwork.chainId));

      // Set connected account
      setAddress(accounts[0]);

      Logger.info("Wallet connected successfully", {
        address: accounts[0],
        chainId: Number(finalNetwork.chainId),
      });
    } catch (err: unknown) {
      // Handle user rejected request
      if (hasErrorCode(err, 4001)) {
        const error = new Error("User rejected wallet connection");
        setError(error);
        throw error;
      }

      // Handle other errors
      const message = err?.message || "Failed to connect wallet";
      const error = new Error(message);
      Logger.error("Wallet connection failed", { error });
      setError(error);
      throw error;
    } finally {
      setIsConnecting(false);
    }
  }, []);

  const disconnect = useCallback(async () => {
    try {
      // Clear state immediately
      setProvider(null);
      setAddress(null);
      setChainId(null);
      setError(null);

      // Most wallets don't have a disconnect method, but we can try various approaches
      if (window.ethereum) {
        try {
          // Try the WalletConnect disconnect method if available
          if (typeof window.ethereum.disconnect === "function") {
            await window.ethereum.disconnect();
          }
          // Try to clear permissions (MetaMask)
          else if (typeof window.ethereum.request === "function") {
            try {
              await window.ethereum.request({
                method: "wallet_revokePermissions",
                params: [{ eth_accounts: {} }],
              });
            } catch (revokeError) {
              // Silently ignore if method doesn't exist
              Logger.info("Revoke permissions not supported", {
                error: revokeError,
              });
            }
          }
        } catch (walletError) {
          // Log but don't throw - state is already cleared
          Logger.info("Wallet-specific disconnect failed, but state cleared", {
            error: walletError,
          });
        }
      }

      Logger.info("Wallet disconnected successfully");
    } catch (err) {
      Logger.error("Error during wallet disconnect", { error: err });
      // Don't throw error - we still want to clear the state
    }
  }, []);

  const switchChain = useCallback(async (targetChainId: number) => {
    if (typeof window.ethereum === "undefined") {
      throw new Error("Please install MetaMask to switch networks");
    }

    try {
      await window.ethereum.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: `0x${targetChainId.toString(16)}` }],
      });
      Logger.info("Switched network", { chainId: targetChainId });
    } catch (error: unknown) {
      // If the chain hasn't been added to MetaMask
      if (error.code === 4902) {
        try {
          await window.ethereum.request({
            method: "wallet_addEthereumChain",
            params: [MOONBASE_CHAIN_INFO],
          });
          Logger.info("Added Moonbase Alpha network");
        } catch (addError) {
          Logger.error("Failed to add network", { error: addError });
          throw new Error("Failed to add Moonbase Alpha network");
        }
      } else {
        Logger.error("Failed to switch network", { error });
        throw error;
      }
    }
  }, []);

  return (
    <Web3Context.Provider
      value={{
        provider,
        address,
        chainId,
        isConnected: !!address,
        isConnecting,
        connect,
        disconnect,
        error,
        switchChain,
      }}
    >
      {children}
    </Web3Context.Provider>
  );
}

export function useWeb3() {
  const context = useContext(Web3Context);
  if (!context) {
    throw new Error("useWeb3 must be used within a Web3Provider");
  }
  return context;
}

import { useState } from 'react';
import { useToast } from '../contexts/ToastContext';
import { supabase } from '../lib/supabase';
import { useProfile } from './useProfile';

interface CharityVerification {
  id: string;
  charity_id: string;
  status: 'pending' | 'approved' | 'rejected';
  documents: Array<{
    type: string;
    url: string;
  }>;
}

export function useAdminPanel() {
  const [loading, setLoading] = useState(false);
  const { showToast } = useToast();
  const { profile } = useProfile();

  const fetchPendingVerifications = async () => {
    if (!profile?.id) return [];

    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('charity_verifications')
        .select(`
          id,
          charity_id,
          status,
          charity_documents (
            document_type,
            document_url
          )
        `)
        .eq('status', 'pending');

      if (error) throw error;
      return data;
    } catch (error) {
      showToast('error', 'Failed to fetch verifications');
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const updateVerificationStatus = async (
    verificationId: string,
    status: 'approved' | 'rejected',
    reason?: string
  ) => {
    try {
      setLoading(true);
      const { error } = await supabase
        .from('charity_verifications')
        .update({
          status,
          reviewed_at: new Date().toISOString(),
          review_notes: reason
        })
        .eq('id', verificationId);

      if (error) throw error;
      showToast('success', `Verification ${status} successfully`);
    } catch (error) {
      showToast('error', 'Failed to update verification status');
      throw error;
    } finally {
      setLoading(false);
    }
  };

  return {
    fetchPendingVerifications,
    updateVerificationStatus,
    loading
  };
}

import { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useToast } from '@/contexts/ToastContext';
import { supabase } from '@/lib/supabase';
import { validateAuthInput } from '@/utils/validation';
import { UserType } from '@/types/auth';
import { Logger } from '@/utils/logger';
import { RateLimiter } from '@/utils/security/rateLimiter';
import { useAuth as useAuthContext } from '@/contexts/AuthContext';
import { useWeb3 } from '@/contexts/Web3Context';

const MAX_LOGIN_ATTEMPTS = 5;
const LOCKOUT_DURATION = 15 * 60 * 1000; // 15 minutes

export function useAuth() {
  const authContext = useAuthContext();
  const { disconnect } = useWeb3();
  const [loading, setLoading] = useState(false);
  const { showToast } = useToast();
  const navigate = useNavigate();
  const rateLimiter = RateLimiter.getInstance();

  const login = async (email: string, password: string, accountType: 'donor' | 'charity') => {
    try {
      setLoading(true);
      validateAuthInput(email, password);

      // Check rate limiting
      if (rateLimiter.isRateLimited(email, true)) {
        throw new Error(`Too many login attempts. Please try again in ${LOCKOUT_DURATION / 60000} minutes.`);
      }

      try {
        await authContext.login(email, password, accountType);

        // Reset rate limiting on successful login
        rateLimiter.reset(email);
      } catch (authError) {
        // Log the detailed error
        Logger.error('Auth context login failed', { 
          error: authError instanceof Error ? 
            { message: authError.message, stack: authError.stack } : 
            authError,
          email
        });
        throw authError;
      }
    } catch (error) {
      rateLimiter.increment(email);
      const message = error instanceof Error ? error.message : 'Failed to sign in';

      // If account type mismatch, disconnect wallet
      if (message.includes('registered as a') || message.includes('account type')) {
        // Disconnect wallet
        await disconnect();
      }

      showToast('error', 'Authentication Error', message);
      Logger.error('Login failed', { 
        error: error instanceof Error ? 
          { message: error.message, stack: error.stack } : 
          error, 
        email 
      });
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const register = async (
    email: string,
    password: string,
    type: UserType,
    metadata = {}
  ) => {
    try {
      setLoading(true);
      validateAuthInput(email, password);

      await authContext.register(email, password, type, metadata);

      // Navigate to the appropriate login page
      navigate(`/login?type=${type}`);
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Failed to register';
      showToast('error', 'Registration Error', message);
      Logger.error('Registration failed', { 
        error: error instanceof Error ? 
          { message: error.message, stack: error.stack } : 
          error, 
        email, 
        type 
      });
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const resetPassword = async (email: string) => {
    try {
      setLoading(true);
      await authContext.resetPassword(email);
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Failed to send reset email';
      showToast('error', 'Reset Password Error', message);
      Logger.error('Password reset failed', { 
        error: error instanceof Error ? 
          { message: error.message, stack: error.stack } : 
          error, 
        email 
      });
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const sendUsernameReminder = async (email: string) => {
    try {
      setLoading(true);
      await authContext.sendUsernameReminder(email);
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Failed to send username reminder';
      showToast('error', 'Username Reminder Error', message);
      Logger.error('Username reminder failed', { 
        error: error instanceof Error ? 
          { message: error.message, stack: error.stack } : 
          error, 
        email 
      });
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const logout = async () => {
    try {
      setLoading(true);

      // Disconnect wallet first
      await disconnect();

      // Then logout from auth
      await authContext.logout();

      // Use window.location to stay on the same domain
      window.location.href = `${window.location.origin}/`;
    } catch (error) {
      const message = error instanceof Error ? error.message : 'Failed to log out';
      showToast('error', 'Logout Error', message);
      Logger.error('Logout failed', { 
        error: error instanceof Error ? 
          { message: error.message, stack: error.stack } : 
          error 
      });
      throw error;
    } finally {
      setLoading(false);
    }
  };

  return {
    ...authContext,
    login,
    register,
    resetPassword,
    sendUsernameReminder,
    logout,
    loading
  };
}

import { useState, useEffect } from 'react';
import { useToast } from '../contexts/ToastContext';
import { supabase } from '../lib/supabase';
import { useProfile } from './useProfile';

interface DonationMetrics {
  totalDonated: number;
  donationCount: number;
  averageDonation: number;
  impactMetrics: Record<string, number>;
}

interface TimeseriesData {
  date: string;
  amount: number;
}

export function useDonationAnalytics() {
  const [metrics, setMetrics] = useState<DonationMetrics | null>(null);
  const [timeseriesData, setTimeseriesData] = useState<TimeseriesData[]>([]);
  const [loading, setLoading] = useState(false);
  const { showToast } = useToast();
  const { profile } = useProfile();

  const fetchAnalytics = async () => {
    if (!profile?.id) return;

    try {
      setLoading(true);

      // Fetch aggregate metrics
      const { data: metricsData, error: metricsError } = await supabase
        .rpc('get_donation_metrics', { user_id: profile.id });

      if (metricsError) throw metricsError;

      // Fetch timeseries data
      const { data: timeseriesData, error: timeseriesError } = await supabase
        .rpc('get_donation_timeseries', { user_id: profile.id });

      if (timeseriesError) throw timeseriesError;

      setMetrics(metricsData);
      setTimeseriesData(timeseriesData);
    } catch (error) {
      showToast('error', 'Failed to fetch analytics');
      throw error;
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAnalytics();
  }, [profile?.id]);

  return {
    metrics,
    timeseriesData,
    loading,
    refreshAnalytics: fetchAnalytics
  };
}

import React, { useState } from 'react';
import { validateAmount } from '@/utils/validation';
import { Logger } from '@/utils/logger';

interface TransactionFormConfig {
  onSuccess?: () => void;
}

export function useTransactionForm({ onSuccess }: TransactionFormConfig) {
  const [amount, setAmount] = useState('');
  const [validationError, setValidationError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent, submitFn: (amount: string) => Promise<void>) => {
    e.preventDefault();
    setValidationError('');

    const numAmount = parseFloat(amount);
    if (!validateAmount(numAmount)) {
      setValidationError('Please enter a valid amount between 0 and 1,000,000');
      return;
    }

    try {
      setLoading(true);
      await submitFn(amount);
      setAmount('');
      onSuccess?.();

      Logger.info('Transaction successful', {
        amount
      });
    } catch (err) {
      Logger.error('Transaction failed', {
        error: err instanceof Error ? err.message : String(err),
        amount
      });
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return {
    amount,
    setAmount,
    validationError,
    loading,
    handleSubmit
  };
}

import { useState, useEffect } from 'react';
import { useToast } from '../contexts/ToastContext';
import { supabase } from '../lib/supabase';
import { useProfile } from './useProfile';

export interface Transaction {
  id: string;
  type: 'donation' | 'withdrawal';
  amount: number;
  status: 'pending' | 'completed' | 'failed';
  txHash?: string;
  created_at: string;
  metadata?: Record<string, unknown>;
}

export function useTransactionTracking() {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(false);
  const { showToast } = useToast();
  const { profile } = useProfile();

  const trackTransaction = async (
    type: Transaction['type'],
    amount: number,
    txHash?: string,
    metadata?: Record<string, unknown>
  ) => {
    if (!profile?.id) throw new Error('Profile not found');

    try {
      const { error } = await supabase
        .from('transactions')
        .insert({
          user_id: profile.id,
          type,
          amount,
          status: 'pending',
          tx_hash: txHash,
          metadata
        });

      if (error) throw error;

      showToast('success', 'Transaction tracked successfully');
      await fetchTransactions();
    } catch (error) {
      showToast('error', 'Failed to track transaction');
      throw error;
    }
  };

  const fetchTransactions = async () => {
    if (!profile?.id) return;

    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('transactions')
        .select('*')
        .eq('user_id', profile.id)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setTransactions(data);
    } catch (error) {
      showToast('error', 'Failed to fetch transactions');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTransactions();
  }, [profile?.id]);

  return {
    transactions,
    trackTransaction,
    loading
  };
}

import { Logger } from '@/utils/logger';
import { CHAIN_IDS } from '@/config/contracts';

export interface WalletProvider {
  name: string;
  icon: string;
  isInstalled: () => boolean;
  isConnected: (address: string) => Promise<boolean>;
  connect: () => Promise<string>;
  disconnect: () => Promise<void>;
  switchChain: (chainId: number | string) => Promise<void>;
}

class EVMWalletBase implements WalletProvider {
  name: string;
  icon: string;
  protected provider: unknown;

  constructor(name: string, icon: string, provider: unknown) {
    this.name = name;
    this.icon = icon;
    this.provider = provider;
  }

  isInstalled(): boolean {
    return !!this.provider;
  }

  async isConnected(address: string): Promise<boolean> {
    try {
      const accounts = await this.provider.request({ method: 'eth_accounts' });
      return accounts?.includes(address) || false;
    } catch {
      return false;
    }
  }

  async connect(): Promise<string> {
    try {
      const accounts = await this.provider.request({
        method: 'eth_requestAccounts'
      });

      if (!accounts?.length) {
        throw new Error('No accounts found');
      }

      return accounts[0];
    } catch (error) {
      Logger.error(`${this.name} connection failed`, { error });
      throw error;
    }
  }

  async disconnect(): Promise<void> {
    // Most EVM wallets don't have a disconnect method
    return Promise.resolve();
  }

  async switchChain(chainId: number): Promise<void> {
    try {
      await this.provider.request({
        method: 'wallet_switchEthereumChain',
        params: [{ chainId: `0x${chainId.toString(16)}` }]
      });
    } catch (error: unknown) {
      if (error.code === 4902) {
        // Chain not added, add it
        await this.addChain(chainId);
      } else {
        throw error;
      }
    }
  }

  protected async addChain(chainId: number): Promise<void> {
    const chainParams = this.getChainParams(chainId);
    if (!chainParams) throw new Error('Unsupported chain');

    await this.provider.request({
      method: 'wallet_addEthereumChain',
      params: [chainParams]
    });
  }

  protected getChainParams(chainId: number) {
    const chains = {
      [CHAIN_IDS.MOONBASE]: {
        chainId: `0x${CHAIN_IDS.MOONBASE.toString(16)}`,
        chainName: 'Moonbase Alpha',
        nativeCurrency: {
          name: 'DEV',
          symbol: 'DEV',
          decimals: 18
        },
        rpcUrls: ['https://rpc.api.moonbase.moonbeam.network'],
        blockExplorerUrls: ['https://moonbase.moonscan.io/']
      },
      [CHAIN_IDS.MOONBEAM]: {
        chainId: `0x${CHAIN_IDS.MOONBEAM.toString(16)}`,
        chainName: 'Moonbeam',
        nativeCurrency: {
          name: 'GLMR',
          symbol: 'GLMR',
          decimals: 18
        },
        rpcUrls: ['https://rpc.api.moonbeam.network'],
        blockExplorerUrls: ['https://moonbeam.moonscan.io/']
      },
      [CHAIN_IDS.ASTAR]: {
        chainId: `0x${CHAIN_IDS.ASTAR.toString(16)}`,
        chainName: 'Astar',
        nativeCurrency: {
          name: 'ASTR',
          symbol: 'ASTR',
          decimals: 18
        },
        rpcUrls: ['https://astar.api.onfinality.io/public'],
        blockExplorerUrls: ['https://blockscout.com/astar']
      },
      [CHAIN_IDS.POLYGON]: {
        chainId: `0x${CHAIN_IDS.POLYGON.toString(16)}`,
        chainName: 'Polygon',
        nativeCurrency: {
          name: 'MATIC',
          symbol: 'MATIC',
          decimals: 18
        },
        rpcUrls: ['https://polygon-rpc.com'],
        blockExplorerUrls: ['https://polygonscan.com/']
      }
    };
    return chains[chainId as keyof typeof chains];
  }
}

class MetaMaskWallet extends EVMWalletBase {
