import { useState, useEffect } from 'react';
import { supabase } from '@/lib/supabase';
import { useAuth } from '@/contexts/AuthContext';
import { useWeb3 } from '@/contexts/Web3Context';
import { WalletAlias } from '@/types/user';
import { Logger } from '@/utils/logger';
import { useToast } from '@/contexts/ToastContext';

export function useWalletAlias() {
  const { user } = useAuth();
  const { address } = useWeb3();
  const { showToast } = useToast();
  const [alias, setAlias] = useState<string>('');
  const [aliases, setAliases] = useState<WalletAlias[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [retryCount, setRetryCount] = useState(0);
  const MAX_RETRIES = 3;
  const RETRY_DELAY = 1000; // 1 second base delay

  // Fetch the current user's wallet alias
  useEffect(() => {
    if (user && address) {
      fetchWalletAlias();
    }
  }, [user, address]);

  // Fetch all wallet aliases for the current user
  useEffect(() => {
    if (user) {
      fetchUserAliases();
    }
  }, [user]);

  const fetchWalletAlias = async () => {
    if (!user || !address) return;

    try {
      setLoading(true);
      setError(null);

      Logger.info('Fetching wallet alias', { address });

      const { data, error: fetchError } = await supabase
        .from('wallet_aliases')
        .select('alias')
        .eq('user_id', user.id)
        .eq('wallet_address', address)
        .maybeSingle();

      if (fetchError && fetchError.code !== 'PGRST116') { // PGRST116 is "no rows returned" error
        throw fetchError;
      }

      setAlias(data?.alias || '');

      // Reset retry count on success
      setRetryCount(0);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : String(err);
      const errorStack = err instanceof Error ? err.stack : undefined;

      Logger.error('Error fetching wallet alias', { 
        error: errorMessage,
        stack: errorStack,
        address,
        retryCount
      });

      setError('Failed to fetch wallet alias');

      // Implement retry with exponential backoff
      if (retryCount < MAX_RETRIES) {
        const delay = RETRY_DELAY * Math.pow(2, retryCount);
        Logger.info(`Retrying wallet alias fetch in ${delay}ms (attempt ${retryCount + 1}/${MAX_RETRIES})`);

        setTimeout(() => {
          setRetryCount(prev => prev + 1);
          fetchWalletAlias();
        }, delay);
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchUserAliases = async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError(null);

      Logger.info('Fetching user aliases');

      const { data, error: fetchError } = await supabase
        .from('wallet_aliases')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false });

      if (fetchError) {
        throw fetchError;
      }

      setAliases(data || []);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : String(err);
      const errorStack = err instanceof Error ? err.stack : undefined;

      Logger.error('Error fetching user aliases', { 
        error: errorMessage,
        stack: errorStack
      });

      setError('Failed to fetch wallet aliases');
    } finally {
      setLoading(false);
    }
  };

  const setWalletAlias = async (newAlias: string) => {
    if (!user || !address) {
      setError('User or wallet not connected');
      return false;
    }

    if (!newAlias.trim()) {
      setError('Alias cannot be empty');
      return false;
    }

    try {
      setLoading(true);
      setError(null);

      Logger.info('Setting wallet alias', { address, alias: newAlias });

      // Check if alias is already taken
      const { data: existingAlias, error: checkError } = await supabase
        .from('wallet_aliases')
        .select('id')
        .eq('alias', newAlias)
        .not('user_id', 'eq', user.id) // Allow user to reuse their own aliases
        .single();

      if (checkError && checkError.code !== 'PGRST116') { // PGRST116 is "no rows returned" error
        throw checkError;
      }

      if (existingAlias) {
        setError('This alias is already taken');
        return false;
      }

      // Check if this wallet already has an alias for this user
      const { data: existingWalletAlias, error: walletCheckError } = await supabase
        .from('wallet_aliases')
        .select('id')
        .eq('user_id', user.id)
        .eq('wallet_address', address)
        .maybeSingle();

      if (walletCheckError && walletCheckError.code !== 'PGRST116') {
        throw walletCheckError;
      }

      let result;

      if (existingWalletAlias) {
        // Update existing alias
        result = await supabase
          .from('wallet_aliases')
          .update({ alias: newAlias, updated_at: new Date().toISOString() })
          .eq('id', existingWalletAlias.id);
      } else {
        // Create new alias
        result = await supabase
          .from('wallet_aliases')
          .insert({
            user_id: user.id,
            wallet_address: address,
            alias: newAlias
          });
      }

      if (result.error) {
        throw result.error;
      }

      setAlias(newAlias);
      await fetchUserAliases(); // Refresh the list of aliases
      showToast('success', 'Wallet alias updated', 'Your wallet alias has been successfully updated');
      return true;
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to set wallet alias';
      const errorStack = err instanceof Error ? err.stack : undefined;

      Logger.error('Error setting wallet alias', { 
        error: message,
        stack: errorStack
      });

      setError(message);
      showToast('error', 'Error', message);
      return false;
    } finally {
      setLoading(false);
    }
  };

  const deleteWalletAlias = async (aliasId: string) => {
    if (!user) {
      setError('User not connected');
      return false;
    }

    try {
      setLoading(true);
      setError(null);

      Logger.info('Deleting wallet alias', { aliasId });

      const { error: deleteError } = await supabase
        .from('wallet_aliases')
        .delete()
        .eq('id', aliasId)
        .eq('user_id', user.id); // Ensure user can only delete their own aliases

      if (deleteError) {
        throw deleteError;
      }

      // Refresh the list of aliases
      await fetchUserAliases();

      // If the deleted alias was for the current wallet, clear the current alias
      if (address) {
        await fetchWalletAlias();
      }

      showToast('success', 'Wallet alias deleted', 'Your wallet alias has been successfully removed');
      return true;
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to delete wallet alias';
      const errorStack = err instanceof Error ? err.stack : undefined;

      Logger.error('Error deleting wallet alias', { 
        error: message,
        stack: errorStack
      });

      setError(message);
      showToast('error', 'Error', message);
      return false;
    } finally {
      setLoading(false);
    }
  };

  // Get alias for any wallet address (for display in leaderboards, etc.)
  const getAliasForAddress = async (walletAddress: string): Promise<string | null> => {
    try {
      Logger.info('Getting alias for address', { walletAddress });

      const { data, error } = await supabase
        .from('wallet_aliases')
        .select('alias')
        .eq('wallet_address', walletAddress)
        .maybeSingle();

      if (error) {
        Logger.warn('No alias found for address', { walletAddress, error: error.message });
        return null;
      }

      return data?.alias || null;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : String(err);
      const errorStack = err instanceof Error ? err.stack : undefined;

      Logger.error('Error getting alias for address', { 
        error: errorMessage,
        stack: errorStack,
        address: walletAddress
      });

      return null;
    }
  };

  return {
    alias,
    aliases,
    loading,
    error,
    setWalletAlias,
    deleteWalletAlias,
    getAliasForAddress,
    refreshAliases: fetchUserAliases
  };
}

import { useState, useEffect } from 'react';
import { supabase } from '@/lib/supabase';
import { useProfile } from '@/hooks/useProfile';
import { WithdrawalRequest } from '@/types/charity';
import { Logger } from '@/utils/logger';

interface Withdrawal {
  id: string;
  amount: number;
  status: 'pending' | 'approved' | 'rejected';
  created_at: string;
  processed_at: string | null;
}

export const useWithdrawals = () => {
  const { profile } = useProfile();
  const [withdrawals, setWithdrawals] = useState<Withdrawal[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchWithdrawals = async () => {
      if (!profile?.id) return;

      try {
        const { data, error: fetchError } = await supabase
          .from('withdrawal_requests')
          .select('*')
          .eq('charity_id', profile.id)
          .order('created_at', { ascending: false });

        if (fetchError) throw fetchError;
        setWithdrawals(data || []);
      } catch (err) {
        setError('Error fetching withdrawals');
        Logger.error('Error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchWithdrawals();
  }, [profile]);

  const requestWithdrawal = async (amount: number) => {
    if (!profile?.id) return;

    try {
      setLoading(true);
      const { error: withdrawalError, data } = await supabase
        .from('withdrawal_requests')
        .insert({
          charity_id: profile.id,
          amount,
          status: 'pending'
        })
        .select()
        .single();

      if (withdrawalError) throw withdrawalError;
      setWithdrawals(prev => [data, ...prev]);
    } catch (err) {
      setError('Error requesting withdrawal');
      console.error('Error:', err);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return { withdrawals, requestWithdrawal, loading, error };
};

import { useState } from 'react';
import { useContract } from './useContract';
import { useWeb3 } from '@/contexts/Web3Context';
import { parseEther } from 'ethers';
import { Logger } from '@/utils/logger';
import { trackTransaction } from '@/lib/sentry';

export enum DonationType {
  NATIVE = 'native',
  TOKEN = 'token'
}

export enum PoolType {
  DIRECT = 'direct',
  EQUITY = 'equity'
}

interface DonationParams {
  charityAddress: string;
  amount: string;
  type: DonationType;
  tokenAddress?: string;
  poolType?: PoolType;
}

export function useDonation() {
  const { contract } = useContract('donation');
  const { address } = useWeb3();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const donate = async ({ charityAddress, amount, type, tokenAddress, poolType = PoolType.DIRECT }: DonationParams) => {
    if (!contract || !address) {
      throw new Error('Contract or wallet not connected');
    }

    // Start Sentry transaction tracking
    const transaction = trackTransaction('donation', {
      amount,
      charityId: charityAddress,
      donationType: type,
      status: 'started'
    });

    try {
      setLoading(true);
      setError(null);

      const parsedAmount = parseEther(amount);

      if (type === DonationType.NATIVE) {
        // For direct donations
        if (poolType === PoolType.DIRECT) {
          // In ethers v6, we need to use the contract.getFunction method
          const donateFunction = contract.getFunction('donate');
          const tx = await donateFunction(charityAddress, {
            value: parsedAmount
          });
          await tx.wait();
        } 
        // For equity pool donations
        else if (poolType === PoolType.EQUITY) {
          // This would call a different contract method for equity pool donations
          // For now, we'll use the same method
          const donateFunction = contract.getFunction('donate');
          const tx = await donateFunction(charityAddress, {
            value: parsedAmount
          });
          await tx.wait();
        }

        Logger.info('Donation successful', {
          amount,
          charity: charityAddress,
          type: 'native',
          poolType
        });

        // Mark transaction as successful
        transaction.finish('ok');
      } else {
        // For token donations, we would need to implement this
        // based on the contract's token donation functionality
        throw new Error('Token donations not yet implemented');
      }
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to process donation';
      setError(message);
      Logger.error('Donation failed', {
        error: err,
        amount,
        charity: charityAddress,
        type
      });

      // Mark transaction as failed
      transaction.finish('error');

      throw err;
    } finally {
      setLoading(false);
    }
  };

  const withdraw = async (amount: string) => {
    if (!contract || !address) {
      throw new Error('Contract or wallet not connected');
    }

    try {
      setLoading(true);
      setError(null);

      const parsedAmount = parseEther(amount);
      const withdrawFunction = contract.getFunction('withdraw');
      const tx = await withdrawFunction(parsedAmount);
      const receipt = await tx.wait();

      Logger.info('Withdrawal successful', {
        amount,
        txHash: receipt.hash
      });

      return receipt.hash;
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to process withdrawal';
      setError(message);
      Logger.error('Withdrawal failed', {
        error: err,
        amount
      });
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return {
    donate,
    withdraw,
    loading,
    error
  };
}

import React, { useState } from 'react';
import { validateAmount } from '@/utils/validation';
import { Logger } from '@/utils/logger';

interface TransactionFormConfig {
  onSuccess?: () => void;
}

export function useTransactionForm({ onSuccess }: TransactionFormConfig) {
  const [amount, setAmount] = useState('');
  const [validationError, setValidationError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent, executeFn: (amount: string) => Promise<void>) => {
    e.preventDefault();
    setValidationError('');

    const numAmount = parseFloat(amount);
    if (!validateAmount(numAmount)) {
      setValidationError('Please enter a valid amount between 0 and 1,000,000');
      return;
    }

    try {
      setLoading(true);
      await executeFn(amount);
      setAmount('');
      onSuccess?.();

      Logger.info('Transaction successful', {
        amount
      });
    } catch (err) {
      Logger.error('Transaction failed', {
        error: err instanceof Error ? err.message : String(err),
        amount
      });
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return {
    amount,
    setAmount,
    validationError,
    loading,
    handleSubmit
  };
}

import { createClient } from '@supabase/supabase-js';
import { ENV } from '../config/env';

// Supabase client configuration
const supabaseUrl = ENV.SUPABASE_URL;
const supabaseAnonKey = ENV.SUPABASE_ANON_KEY;

if (!supabaseUrl || !supabaseAnonKey) {
  throw new Error(
    'Missing Supabase environment variables. Please check VITE_SUPABASE_URL and VITE_SUPABASE_ANON_KEY.'
  );
}

// Create Supabase client with security and performance optimizations
export const supabase = createClient(supabaseUrl, supabaseAnonKey, {
  auth: {
    // Configure auth settings
    autoRefreshToken: true,
    persistSession: true,
    detectSessionInUrl: true,
    flowType: 'pkce', // Use PKCE flow for better security
    // Storage for auth tokens
    storage: {
      getItem: (key: string) => {
        if (typeof window !== 'undefined') {
          return window.localStorage.getItem(key);
        }
        return null;
      },
      setItem: (key: string, value: string) => {
        if (typeof window !== 'undefined') {
          window.localStorage.setItem(key, value);
        }
      },
      removeItem: (key: string) => {
        if (typeof window !== 'undefined') {
          window.localStorage.removeItem(key);
        }
      },
    },
  },
  db: {
    // Database settings
    schema: 'public',
  },
  global: {
    // Global settings
    headers: {
      'X-Client-Info': 'give-protocol-app',
    },
  },
  realtime: {
    // Realtime settings for live updates
    params: {
      eventsPerSecond: 10,
    },
  },
});

// Helper functions for common operations
export const supabaseHelpers = {
  // Auth helpers
  async getCurrentUser() {
    const { data: { user }, error } = await supabase.auth.getUser();
    if (error) throw error;
    return user;
  },

  async signOut() {
    const { error } = await supabase.auth.signOut();
    if (error) throw error;
  },

  async refreshSession() {
    const { data, error } = await supabase.auth.refreshSession();
    if (error) throw error;
    return data;
  },

  // Database helpers
  async handleError(error: Error | unknown, context: string) {
    console.error(`Supabase error in ${context}:`, error);

    // Log to monitoring if available
    if (typeof window !== 'undefined' && 'MonitoringService' in window) {
      const monitoringService = (window as { MonitoringService?: { trackMetric: (event: string, data: Record<string, unknown>) => void } }).MonitoringService;
      if (monitoringService?.trackMetric) {
        monitoringService.trackMetric('supabase_error', {
          context,
          error: error instanceof Error ? error.message : String(error),
          code: error instanceof Error && 'code' in error ? error.code : undefined,
          details: error instanceof Error && 'details' in error ? error.details : undefined,
        });
      }
    }

    throw error;
  },

  // Connection helpers
  async testConnection() {
    try {
      const { error } = await supabase
        .from('_supabase_test')
        .select('*')
        .limit(1);

      if (error && error.code !== 'PGRST116') { // PGRST116 = table not found, which is fine for test
        throw error;
      }

      return true;
    } catch (error) {
      console.warn('Supabase connection test failed:', error);
      return false;
    }
  },
};

