    timestamp: string;
    amount?: number;
  }>;
}

const AdminDashboard: React.FC = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState<AdminStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchAdminStats();
    trackEvent('admin_dashboard_viewed', { userId: user?.id });
  }, [user?.id]);

  const fetchAdminStats = async () => {
    try {
      setLoading(true);
      setError(null);

      // In a real implementation, these would be actual database queries
      // For now, we'll use mock data since the full backend might not be set up
      const mockStats: AdminStats = {
        totalUsers: 1250,
        totalDonations: 89,
        totalCharities: 23,
        totalVolunteers: 156,
        recentActivity: [
          {
            id: '1',
            type: 'donation',
            description: 'New donation to Education for All',
            timestamp: new Date(Date.now() - 1000 * 60 * 30).toISOString(),
            amount: 250
          },
          {
            id: '2',
            type: 'registration',
            description: 'New charity registered: Ocean Cleanup Initiative',
            timestamp: new Date(Date.now() - 1000 * 60 * 60 * 2).toISOString()
          },
          {
            id: '3',
            type: 'verification',
            description: 'Volunteer hours verified for Clean Water Project',
            timestamp: new Date(Date.now() - 1000 * 60 * 60 * 4).toISOString()
          },
          {
            id: '4',
            type: 'donation',
            description: 'Large donation to Climate Action Now',
            timestamp: new Date(Date.now() - 1000 * 60 * 60 * 6).toISOString(),
            amount: 1000
          }
        ]
      };

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      setStats(mockStats);
    } catch (err) {
      console.error('Failed to fetch admin stats:', err);
      setError('Failed to load dashboard data. Please try again.');
      trackEvent('admin_dashboard_error', { 
        error: err instanceof Error ? err.message : String(err),
        userId: user?.id 
      });
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  const formatRelativeTime = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInHours = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) {
      const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));
      return `${diffInMinutes} minutes ago`;
    } else if (diffInHours < 24) {
      return `${diffInHours} hours ago`;
    } else {
      const diffInDays = Math.floor(diffInHours / 24);
      return `${diffInDays} days ago`;
    }
  };

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'donation':
        return '';
      case 'registration':
        return '';
      case 'verification':
        return '';
      default:
